package uz.umarov.todo.dbimport android.content.Contextimport androidx.room.Databaseimport androidx.room.Roomimport androidx.room.RoomDatabaseimport uz.umarov.todo.DB_NAMEimport uz.umarov.todo.models.Todo@Database(entities = [Todo::class], version = 1)abstract class AppDatabase : RoomDatabase() {    abstract fun todoDao(): TodoDao    companion object {        @Volatile        private var INSTANCE: AppDatabase? = null        fun getDatabase(context: Context): AppDatabase {            val tempInstance = INSTANCE            if (tempInstance != null) {                return tempInstance            }            synchronized(this) {                val instance = Room.databaseBuilder(                    context.applicationContext,                    AppDatabase::class.java,                    DB_NAME                ).build()                INSTANCE = instance                return instance            }        }    }}