package uz.umarov.todo.dbimport androidx.lifecycle.LiveDataimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.Queryimport uz.umarov.todo.models.Todo@Daointerface TodoDao {    @Insert()    suspend fun insertTask(todoModel: Todo):Long    @Query("Select * from Todo where isFinished == 0")    fun getTask(): LiveData<List<Todo>>    @Query("Update Todo Set isFinished = 1 where id=:uid")    fun finishTask(uid:Long)    @Query("Delete from Todo where id=:uid")    fun deleteTask(uid:Long)}